import { Form, Icon, Input, Button, Checkbox, message , notification} from "antd";
import "./index.css";
import React from "react";
import { Row, Col } from "antd";
// import {Animated} from "react-animated-css";
// import logo from '../image/logo.jpg'
import { withRouter } from "react-router";
import { Mutation, Query } from "react-apollo";
import { SIGNIN_USER, GET_ALL_USER } from "../../queries/index";
import { Alert, Result } from "antd";
import Spinner from "../spinner/index";
import { Redirect} from "react-router-dom";
import Notification from '../dashboard/notification/';

// import BackgroundImage from 'react-background-image-loader';

const FormItem = Form.Item;

const inintialState = {
  username: "",
  password: "",
  message: "Sucessfuly Logged In ",
  isLoading: true,
  redirectToReferrer: false
};
const fakeAuth = {
  isAuthenticated: true,
  authenticate(cb){
    this.isAuthenticated = true
    setTimeout(cb, 100)
  },
  signout(cb) {
    this.isAuthenticated = false
    setTimeout(cb, 100)
  }
}

class Login extends React.Component {
  state = { ...inintialState };

  componentDidMount() {
    setTimeout(() => this.setState({ isLoading: false }), 5000);
    //    setTimeout(
    //   function() {
    //     this.setState({ isLoading: false });
    //   }.bind(this),
    //   2000
    // );
  }

  login = () => {
    fakeAuth.authenticate( () => {
      this.setState(() =>({
        redirectToReferrer: true
      }))
    })
  }

  clearState =()=>{
    this.setState({ ...inintialState });
}

  handleChange = event => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  handleSubmit = (event, signinUser) => {
    event.preventDefault();
    this.setState({ isLoading: true }, () => {
    signinUser().then(({ data }) => {

          // this.setState({ isLoading: false });
          console.log(data);
          //console.log('Data:::', this.state)
          localStorage.setItem("token", data.signinUser.token);
          message.success(this.state.message);
          this.props.history.push("/home");

          console.log("Data:::", this.state);


        })
        .catch(function (error) {

          message.error(error.message);

          console.log("i am error", error.message);


        });


      this.clearState();
    });
  };

  validateForm = () => {
    const { username, password } = this.state;
    const isInvalid = !username || !password;
    return isInvalid;
  };

  render() {
    const { redirectToReferrer } = this.state ;
    const { from } = this.props.location.state ||  { from: { pathname: '/'}}
    if(redirectToReferrer === true ){
      return( <Redirect to= '/' />)
    }


    if(localStorage.getItem('token')){
      return( <Notification/>)

    }
    const { username, password, isLoading } = this.state;

    if (isLoading) {
      return (
        <div>
          {/* <image src={{ backgroundImage: "../image/logo.jpg" }} /> */}
          <Spinner />
        </div>
      );
   }

    // else {
      return (
        <Query query={GET_ALL_USER}>
         {({ data, loading }) => {
           console.log("it is a data",data);
           if(loading) return  <Spinner />

        return (
        <div className='conatainer' >



          <Row >
            <Col span={12} offset={6}>
              <div className="login-form{" style={{ display: "flex" }}>
                <div style={{ margin: 130 }}>
                  <Mutation
                    mutation={SIGNIN_USER}
                    variables={{ username, password }}
                  >
                    {(signinUser, { data, Loading, error }) => {
                      if(Loading){
                        return <Spinner />
                      }
                      return (
                        <Form
                          className="login-form"
                          style={{ width: 400, backgroundColor: "white" }}
                          onSubmit={event =>
                            this.handleSubmit(event, signinUser)
                          }
                        >
                          <div style={{ paddingTop: 30 }}>
                            <strong>
                              <span
                                className="login100-form-title p-b-51"
                                style={{ paddingLeft: 80 }}
                              >
                                <img
                                  style={{ width: 80 }}
                                  src={require("../image/logo.jpg")}
                                  alt=""
                                />
                                Signin
                              </span>
                            </strong>
                          </div>
                          <FormItem style={{ paddingLeft: 50, paddingTop: 50 }}>
                            <FormItem>
                              <Input
                                className="input-sigin"
                                name="username"
                                value={username}
                                onChange={event => this.handleChange(event)}
                                prefix={
                                  <Icon
                                    type="user"
                                    style={{ color: "rgba(0,0,0,.25)" }}
                                  />
                                }
                                placeholder="Username"
                              />
                            </FormItem>
                            <FormItem>
                              <Input
                                className="input-sigin"
                                name="password"
                                value={password}
                                onChange={event => this.handleChange(event)}
                                prefix={
                                  <Icon
                                    type="lock"
                                    style={{ color: "rgba(0,0,0,.25)" }}
                                  />
                                }
                                type="password"
                                placeholder="Password"
                              />
                            </FormItem>
                          </FormItem>
                          <FormItem>
                            <Checkbox
                              className="checkBox"
                              style={{ float: "left" }}
                            >
                              Remember me
                            </Checkbox>
                          </FormItem>

                          <FormItem style={{ paddingLeft: 80 }}>
                            <Button
                              type="primary"
                              htmlType="submit"
                              className="login-form-button"
                              disabled={ Loading  || this.validateForm()}
                              style={{ width: 210, paddingLeft: 10 }}

                              // onClick={event =>this.handleSubmit(event, signinUser)}
                            >
                              <Icon type="login" />
                              Sign in
                            </Button>
                            <FormItem>
                              {/* {error && <Result
              type="error"
              title="Error"
              description={error.message}
              actions={<Button size="large" type="primary">OK</Button>}
            />} */}
                              {error && (
                                <Alert
                                  style={{ marginRight: 20 }}
                                  message="Error"
                                  description={error.message}
                                  type="error"
                                  showIcon
                                />
                              )}
                            </FormItem>
                          </FormItem>
                          <FormItem className="register">
                            <a href="/CreateEmployee" style={{ float: "left" }}>
                              Create Employee!
                            </a>
                          </FormItem>
                        </Form>


                      );
                    }}
                  </Mutation>
                </div>
              </div>
            </Col>
          </Row>

        </div>)
         }}
        </Query>
      );

  // }
  }
}

export default withRouter(Login);
